  /**
   * 字符串转换unicode
   */
  public static String string2Unicode(String string) {
   
      StringBuffer unicode = new StringBuffer();
   
      for (int i = 0; i < string.length(); i++) {
   
          // 取出每一个字符
          char c = string.charAt(i);
          if (c >= 913 && c <= 65509) {// 汉字范围 \u0391-\uffe5 (中文)  
	          // 转换为unicode
	          unicode.append("\\u" + Integer.toHexString(c));
          }
          else {  
        	  unicode.append(string.charAt(i));  
          } 
      }
   
      return unicode.toString();
  }
   
  /**
   * unicode 转字符串
   */
  public static String unicode2String(String unicode) {
   
      StringBuffer string = new StringBuffer();
   
      String[] hex = unicode.split("\\\\u");
   
      for (int i = 1; i < hex.length; i++) {
   
          // 转换出每一个代码点
          int data = Integer.parseInt(hex[i], 16);
   
          // 追加成string
          string.append((char) data);
      }
   
      return string.toString();
  }
  
  private String big5ToGb_0(String str) {
    if ((str == null) || (str.length() == 0))
      return str;
    char[] ca = str.toCharArray();
    boolean changed = false;
    for (int i = 0; i < ca.length; i++) {
      char c = ca[i];
      if ((c < '万') || (c > 40863))
        continue;
      char c1 = this.cBig5ToGB[(c - '万')];
      if (c1 == 0)
        continue;
      ca[i] = c1;
      changed = true;
    }
    if (!changed)
      return str;
    return new String(ca);
  }
  public static String gbToBig5(String str) {
    return getInstance().gbToBig5_0(str);
  }
  private String gbToBig5_0(String str) {
    if ((str == null) || (str.length() == 0))
      return str;
    char[] ca = str.toCharArray();
    boolean changed = false;
    for (int i = 0; i < ca.length; i++) {
      char c = ca[i];
      if ((c < '万') || (c > 40863))
        continue;
      char c1 = this.cGBToBig5[(c - '万')];
      if (c1 == 0)
        continue;
      ca[i] = c1;
      changed = true;
    }
    if (!changed)
      return str;
    return new String(ca);
  }
  private static ISNConvert getInstance() {
    if (cjtf == null) {     
      synchronized (ISNConvert.class) {
		if(cjtf == null)
			cjtf = new ISNConvert();
      }
    }
    return cjtf;
  }

